    public void doRecordShow(){
        
        if(isNextRecordAvailiable){
            clearSearchTableResults();
            showRecord(cursor);
        }
        else
            JOptionPane.showMessageDialog(null,"VRMS-Core-Engine is unable to \ndetermine record counter to move pointer to");
            
        
    }
