    public void search(int option){
        String query = "";
        String status = "";
        cursor=0;
        isNextRecordAvailiable = true;
        DB_Engine db_engine = new DB_Engine();
        
        
        if(option == SystemConstants.SEARCH_OPTION_NAME_HNAME)
        {
     
            String name = tfO1name.getText();
            String hname = tfO1HName.getText();
            query = "SELECT * FROM TBL_VOTER_RECORD WHERE name = '"+name+"' and HEADNAME = '"+hname+"'";
            status = "Name: "+name+" H-Name: "+hname;
        }
        else if(option == SystemConstants.SEARCH_OPTION_NAME)
        {
            String name = tfO2name.getText();
            query = "SELECT * FROM TBL_VOTER_RECORD WHERE name = '"+name+"'";
            status = "Name: "+name;
        }
        else if(option == SystemConstants.SEARCH_OPTION_HNAME)
        {
            String hname = tfO2HName.getText();
            query = "SELECT * FROM TBL_VOTER_RECORD WHERE HEADNAME = '"+hname+"'";
            status = "H-Name: "+hname;
        }
        else if(option == SystemConstants.SEARCH_OPTION_NAME_STARTING_WITH)
        {
            String name = tfO3name.getText();
            query = "SELECT * FROM TBL_VOTER_RECORD WHERE NAME like '"+name+"%'";
            status = "Name starting with :"+name;
        }
        else if(option == SystemConstants.SEARCH_OPTION_HNAME_STARTING_WITH)
        {
            String name = tfO3HName.getText();
            query = "SELECT * FROM TBL_VOTER_RECORD WHERE NAME like '"+name+"%'";
            status = "Records with Head Name starting with :"+name;
        }
        else if(option == SystemConstants.SEARCH_OPTION_NAME_ENDING_WITH)
        {
            String name = tfO5name.getText();
            query = "SELECT * FROM TBL_VOTER_RECORD WHERE NAME like '%"+name+"'";
            status = "Records with name ending with :"+name;
        }
        else if(option == SystemConstants.SEARCH_OPTION_HNAME_ENDING_WITH)
        {
            String name = tfO5HName.getText();
            query = "SELECT * FROM TBL_VOTER_RECORD WHERE NAME like '%"+name+"'";
            status = "Records with head name ending with :"+name;
        }
        else if(option == SystemConstants.SEARCH_OPTION_NAME_CONTAINS)
        {
            String name = tfO3nameContain.getText();
            query = "SELECT * FROM TBL_VOTER_RECORD WHERE NAME like '%"+name+"%'";
            status = "Records with Name containing :"+name;
        }
        else if(option == SystemConstants.SEARCH_OPTION_HNAME_CONTAINS)
        {
            String name = tfO3HNameContain.getText();
            query = "SELECT * FROM TBL_VOTER_RECORD WHERE NAME like '%"+name+"%'";
            status = "Records with head name containing :"+name;
        }
        else if(option == SystemConstants.SEARCH_OPTION_NAME_AREA)
        {
            String name = tfO4name.getText();
            String area = tfO4nameArea.getText();
            query = "SELECT * FROM TBL_VOTER_RECORD WHERE NAME like '"+name+"'"+"AND AREA like '"+area+"'";
            status = "Records with name: "+name+" and area: "+area;
        }
        else if(option == SystemConstants.SEARCH_OPTION_HNAME_AREA)
        {
            String name = tfO4HName.getText();
            String area = tfO4HNameArea.getText();
            query = "SELECT * FROM TBL_VOTER_RECORD WHERE NAME like '"+name+"' and AREA like '"+area+"'";
            status = "Records with head name: "+name+" and area: "+area;
            
        }
        else
            JOptionPane.showMessageDialog(null,"Unknown Search Option\nSearch type not registered by class SystemConstants");
        
        
        record = db_engine.searchRecord(query);
        searchItem = status;    
        max = record.length;
            
            if(record.length==0)
                JOptionPane.showMessageDialog(null,"Search returned zero results");
            else
            {
                showRecord(cursor);
            
            }
    }
    
    
    public void showRecord(int cursor)
    {
        if(cursor <= record.length-1)
        {
            if(max-cursor > viewSize)
                tfRecordCounter.setText("Record "+(cursor+1)+" To "+(cursor+viewSize)+" of "+record.length+" Records");
            else
                tfRecordCounter.setText("Record "+(cursor+1)+" To "+(max)+" of "+record.length+" Records");
            
            int rowCounter = 0;
            String[] token = new String[4];
            for(int i=cursor;i<=cursor+viewSize-1;i++)
            {
                try{token = record[i].split(","); }catch(Exception e){isNextRecordAvailiable = false;}
                
                if(isNextRecordAvailiable)
                {
                    for(int tokenRecord=0;tokenRecord<=token.length-1;tokenRecord++)
                    {
                        if(token[tokenRecord]==null)
                        token[tokenRecord]="Not Availiable [N/A]";
                    }
                    
                    
                    tblSearchResults.setValueAt((String)token[0],rowCounter,0);
                    tblSearchResults.setValueAt((String)token[1].toUpperCase(),rowCounter,1);
                    tblSearchResults.setValueAt((String)token[2].toUpperCase(),rowCounter,2);
                    tblSearchResults.setValueAt((String)token[3],rowCounter,3);
                    rowCounter++;
                }
                
            }
          
           updateCursor();
          
        }
        else
            JOptionPane.showMessageDialog(null,"VRMS-Core-Engine is unable to \ndetermine record counter to move pointer to");
        
        tfSearchStatus.setText(record.length + " Records found for search item "+searchItem);
    }
    
    public void updateCursor()
    {
        if(viewSize < max-cursor)
        {
            cursor = cursor + viewSize;
        }
        else if(viewSize > max - cursor)
        {
            cursor = cursor + (max - cursor);
            isNextRecordAvailiable = false;
        }
        
    }
    
    public void clearSearchTableResults(){
        
        int rowCount = tblSearchResults.getRowCount();
        for(int i=0;i<=rowCount-1;i++)
            for(int j=0;j<=tblSearchResults.getColumnCount()-1;j++)
                tblSearchResults.setValueAt((String)"",i,j);
        
    }
    public void insertSearchTableRows(int rowCount)
    {
        tblSearchResults.addRowSelectionInterval(5,rowCount);
    }
    
